import React, { useEffect, useState } from 'react';
import { getProvinceData, getCityData } from '../api/amap';
import { generateDescription } from '../api/deepseek';
import './MapView.css';

const MapView = () => {
    const [map, setMap] = useState(null);
    const [provinceInfo, setProvinceInfo] = useState('');
    const [cityInfo, setCityInfo] = useState('');
    const [cities, setCities] = useState([]);

    useEffect(() => {
        const initMap = () => {
            const mapInstance = new window.AMap.Map('mapContainer', {
                zoom: 5,
                center: [123.4294, 41.7968], // 辽宁省中心坐标
            });
            setMap(mapInstance);
        };

        initMap();
    }, []);

    const handleProvinceClick = async (provinceName) => {
        const data = await getProvinceData(provinceName);
        setProvinceInfo(data.introduction);
        setCities(data.cities);
        zoomToProvince(provinceName);
    };

    const handleCityClick = async (cityName) => {
        const data = await getCityData(cityName);
        setCityInfo(data.introduction);
        zoomToCity(cityName);
    };

    const zoomToProvince = (provinceName) => {
        // 逻辑来放大到省级
        map.setZoom(6);
        map.setCenter([123.4294, 41.7968]); // 辽宁省中心坐标
        highlightCities(cities);
    };

    const zoomToCity = (cityName) => {
        // 逻辑来放大到市级
        const cityCoordinates = getCityCoordinates(cityName);
        map.setZoom(8);
        map.setCenter(cityCoordinates);
    };

    const highlightCities = (cities) => {
        // 高亮显示各个市的逻辑
        cities.forEach(city => {
